import React, { useState } from "react";
import Navbar from "./components/Navbar";
import Textform from "./components/Textform";
import About from "./components/About";  
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'; // Import BrowserRouter, Routes, and Route

// import {
//   BrowserRouter as Router,
//   Routes,
//   Route, 
// } from "react-router-dom";  

function App() {
  const [mode, setMode] = useState('light');

  const toggleMode = () => {
    setMode(mode === 'light' ? 'dark' : 'light');
  };

  return (
    <> 
        <Navbar about="about us" mode={mode} toggleMode={toggleMode} />
        <nav>
        <ul>
          <li>
            <Link to="/" className="list"> {/* Change class to className */}
              Home
            </Link> 
          </li>
          <li>
            <Link to="/about" className="list"> {/* Change class to className */}
              About
            </Link>
          </li>
        </ul>
      </nav>
           {/* This is the alias of BrowserRouter i.e. Router */}
           <Router>
                <Routes> 
                    <Route
                        exact
                        path="/"
                        element={<Textform />}
                    />
  
                  <Route
                  path="/about"
                  element={<About />}
                  />    
                  </Routes>
            </Router>
 
    </>
  );
}

export default App;
